def cost_function_gradient(x_data, y_data, y_modeled):
    N = np.size(y_data)
    intercept_gradient = -1*(2/N)*np.sum((y_data-y_modeled))
    slope_gradient = -1*(2/N)*np.sum((y_data-y_modeled)*x_data)
    gradient = np.array([intercept_gradient, slope_gradient])
    return(gradient)
